import { VALIDATE_USER, SIGN_OUT, UNKNOWN_STATE } from '../actions/types';

export default (state = [], action) => {
  switch (action.type) {
    case VALIDATE_USER:
      return action.payload;
    case SIGN_OUT:
      return false;

    default:
      return state;
  }
};
